class Calculos:    def __init__(self, v=float(0), perc=float(0), peri=int(0)):        self.valor = v        self.percentual = perc        self.periodo = peri        self.soma0 = self.valor    def juros_compostos(self):        for c in range(1, self.periodo + 1):            calculo = self.valor * self.percentual / 100            self.soma0 += calculo            self.valor = self.soma0            print(f'No {c:>3}º mês sua divida é:R$ {self.soma0:>9.2f}, '                  f'valor parcela atual R${self.soma0 / self.periodo:>9.2f}')        return self.soma0 / self.periodonum1 = float(input('Informe o valor:  '))num2 = float(input('Informe o valor:  '))num3 = int(input('Informe o valor:  '))a = Calculos(num1, num2, num3)a.juros_compostos()